import React, { useState, useEffect } from "react";
import axios from "axios";

import "../styles/EndGame.scss";

export default function EndGame({
  setIsEnd,
  playerMode,
  setPlayerMode,
  nameForPlayer1,
  nameForPlayer2,
  player1Score,
  setPlayer1Score,
  player2Score,
  setPlayer2Score,
  highScores,
  setHighScores,
}) {
  const [isP1HighScore, setIsP1HighScore] = useState(false);
  const [isP2HighScore, setIsP2HighScore] = useState(false);

  useEffect(() => {
    // Get today's date
    const getTheDate = () => {
      let today = new Date();
      let month = String(today.getMonth() + 1);
      let year = String(today.getFullYear()).slice(-2);

      return `${month}/${year}`;
    };

    // Check if Player 1's score is worthy to post
    if (player1Score > highScores[9].score) {
      axios
        .delete(
          `https://thinkfast-api.herokuapp.com/scores/${highScores[9]._id}`
        )
        .then(function (response) {
          console.log(response);
        })
        .catch(function (error) {
          console.log(error);
        });

      axios
        .post("https://thinkfast-api.herokuapp.com/scores", {
          name: nameForPlayer1,
          score: player1Score,
          date: getTheDate(),
        })
        .then(function (response) {
          setIsP1HighScore(true);
          axios
            .get("https://thinkfast-api.herokuapp.com/scores")
            .then((res) => {
              setHighScores([...res.data]);
              console.log("I would think high scores would be updated");
            })
            .catch((error) => {
              console.log(error);
            });
        })
        .catch(function (error) {
          console.log(error);
        });
    }

    if (player2Score > highScores[9].score) {
      axios
        .delete(
          `https://thinkfast-api.herokuapp.com/scores/${highScores[9]._id}`
        )
        .then(function (response) {
          console.log(response);
        })
        .catch(function (error) {
          console.log(error);
        });

      axios
        .post("https://thinkfast-api.herokuapp.com/scores", {
          name: nameForPlayer2,
          score: player2Score,
          date: getTheDate(),
        })
        .then(function (response) {
          setIsP2HighScore(true);
          axios
            .get("https://thinkfast-api.herokuapp.com/scores")
            .then((res) => {
              setHighScores([...res.data]);
              console.log("I would think high scores would be updated");
            })
            .catch((error) => {
              console.log(error);
            });
        })
        .catch(function (error) {
          console.log(error);
        });
    }
  });

  const restart = () => {
    setIsEnd(false);
    setPlayer1Score(0);
    setPlayer2Score(0);
    setIsP1HighScore(false);
    setIsP2HighScore(false);
    setPlayerMode(null);
  };

  // Messaging for end of game
  let endOfGameMessage;

  // 1 Player Mode
  if (playerMode === 1 && player1Score < 0) {
    endOfGameMessage = (
      <div className="farewell-message">
        <h1>You are such a negative person!</h1>
        <p>{`You scored ${player1Score} points.`}</p>
      </div>
    );
  }

  if (playerMode === 1 && player1Score > 0 && player1Score <= 25) {
    endOfGameMessage = (
      <div className="farewell-message">
        <h1>You need a lot more practice!</h1>
        <p>{`You only scored ${player1Score} points.`}</p>
      </div>
    );
  }

  if (playerMode === 1 && player1Score > 25 && player1Score <= 50) {
    endOfGameMessage = (
      <div className="farewell-message">
        <h1>You are not too bad at this!</h1>
        <p>{`You scored ${player1Score} points.`}</p>
      </div>
    );
  }

  if (playerMode === 1 && player1Score > 50) {
    endOfGameMessage = (
      <div className="farewell-message">
        <h1>You have acheived greatness!</h1>
        <p>{`You scored ${player1Score} points.`}</p>
      </div>
    );
  }

  // 2 Player Mode
  if (playerMode === 2 && player1Score === player2Score) {
    endOfGameMessage = (
      <div className="farewell-message">
        <h1>Wow! It is a draw. </h1>
        <p>{`You both scored ${player1Score} points.`}</p>
      </div>
    );
  }

  if (playerMode === 2 && player1Score > player2Score) {
    endOfGameMessage = (
      <div className="farewell-message">
        <h1>Player 1</h1>
        <h2>wins the game!!</h2>
        <p>{`Beating the competition by ${
          player1Score - player2Score
        } points.`}</p>
      </div>
    );
  }

  if (playerMode === 2 && player1Score < player2Score) {
    endOfGameMessage = (
      <div className="farewell-message">
        <h1>Player 2</h1>
        <h2>wins the game!!</h2>
        <p>{`Beating the competition by ${
          player2Score - player1Score
        } points.`}</p>
      </div>
    );
  }

  return (
    <div id="end-game">
      {endOfGameMessage}
      {isP1HighScore && isP2HighScore ? (
        <h3 className="top-score-message">
          Congratulations {isHighScore === 1 ? nameForPlayer1 : nameForPlayer2},
          you are a Top 10 Scorer!!
        </h3>
      ) : isP1HighScore ? (
        <h3 className="top-score-message">
          Congratulations {nameForPlayer1}, you are a Top 10 Scorer!!
        </h3>
      ) : isP2HighScore ? (
        <h3 className="top-score-message">
          Congratulations {nameForPlayer2}, you are a Top 10 Scorer!!
        </h3>
      ) : null}

      <button onClick={() => restart()}>Play Again?</button>
    </div>
  );
}
